INSERT INTO `pages` (`id`, `num`, `page_id`, `parent_id`, `l_id`, `title`, `page_title`, `content`, `image`, `url_tag`, `keywords`, `description`, `include`, `date`, `share`, `header`, `footer`, `seo_index`) VALUES
(12, 0, 1598598051, 0, 29, 'İstifadəçi şərtləri', 'İstifadəçi şərtləri', '<p dir=\"ltr\"><strong>Correct Technology</strong> 2017-ci ildən təsis edilmiş, &nbsp;veb və mobil proqram həlləri, rəqəmsal marketinq xidmətləri təqdim edən, başqa s&ouml;zlə &nbsp;&quot;İnternet texnologiyalarının b&uuml;t&uuml;n rəngləri&quot; ilə sizlərə xidmət g&ouml;stərən &ouml;lkəmizin ən qabaqcıl şirkətlərindən biridir.&nbsp;</p><p dir=\"ltr\">Gənc, daima &ouml;z&uuml;n&uuml; inkişaf etdirən peşəkar komandamızın, ən son texnologiyalardan istifadə edə prinsipimiz ilə bizə m&uuml;raciət edən şəxsləri hər zaman y&uuml;ksək keyfiyyətli xidmət ilə təmin etmişik.&nbsp;</p><p dir=\"ltr\">Biznesin inkişaf etdirmək və ya fərdi ehtiyac və s. &nbsp;səbəblərindən aslı olmadan, biz ən qısa m&uuml;ddətdə, sizin b&uuml;dcənizə uyğun təklifimizlə sizin internet d&uuml;nyasında &ouml;z m&ouml;vqeyinizi tutmağınıza k&ouml;mək g&ouml;stəririk.</p><p><br></p><h3 dir=\"ltr\"><strong>Misiyamız</strong></h3><p dir=\"ltr\">Biz g&ouml;stərdiyimiz xidmət ilə &ouml;lkəmizdə internet həlləri və rəqəmsal xidmətlər sahəsində m&uuml;ştəri g&ouml;zləntisinin və keyfiyyətli xidmətin &uuml;st standartlarını m&uuml;əyyən edən şirkət olmaq &uuml;&ccedil;&uuml;n &ccedil;alışırıq.&nbsp;</p><p dir=\"ltr\">Bunun &uuml;&ccedil;&uuml;n biz:</p><ul><li>&Uuml;zərində &ccedil;alışdığımız hər bir layihəyə x&uuml;susi diqqətlə yanaşır, m&uuml;raciət&ccedil;inin tələblərini dərindən araşdırdıqdan sonra, onun təlabatına uyğun həllər təqdim edirik.</li><li>Fəaliyyətimizin istənilən mərhələsində, &nbsp;&ouml;z komandamız və m&uuml;raciət edən şəxs arasında y&uuml;ksək səviyyəli əməkdaşlıq və &uuml;nsiyyət qururuq.</li><li>Peşəkar və daima &ouml;z&uuml;n&uuml; inkişaf etdirən komandamızla, sizi d&ouml;vr&uuml;n ən son texnologiyalarından istifadə olunmaqla hazırlanmış və g&uuml;n&uuml;m&uuml;z&uuml;n tələblərinə cavab verən xidmətlərlə təmin edirik.</li></ul><p><br></p><h3 dir=\"ltr\"><strong>Dəyərlərimiz</strong></h3><ul><li>D&uuml;r&uuml;stl&uuml;k və &nbsp;etibar kimi d&uuml;nyəvi olaraq qəbul olunmuş təməl dəyərlərimizə hər zaman sadiq qalırıq</li><li>İnkişaf - hər zaman g&ouml;stərdiyimiz xidmət səviyyəsinin inkişaf etdirməyə &ccedil;alışırıq</li><li>İnnovativlik- g&uuml;n&uuml;m&uuml;z&uuml;n ən son texnoloji yeniliklərini daima izləyir və xidmətimizdə tətbiq edirik</li><li>M&uuml;ştəriy&ouml;n&uuml;ml&uuml; xidmət- hər zaman m&uuml;ştərinin ehtiyaclarını və tələbatını, istəklərinə uyğun xidmət g&ouml;stəririk.</li></ul>', '{\"thumb\":false,\"webp\":true,\"webp_url\":\"51767e1e4bf78b734dd4a18e252de906.jpg\",\"link\":\"51767e1e4bf78b734dd4a18e252de906.jpg\"}', 'istifadeci-sertleri', '', '', 0, '2020-08-28 07:00:51', 'true', '', 'true', 'true');
